local BAIT = {}BAIT.ClassName = "melon"BAIT.Model = "models/props_junk/watermelon01.mdl"BAIT.PositionOffset = Vector(0,0,0)BAIT.Base = "fishing_bait_base_move"BAIT.Description = [[Melons are the the baby's favorite food. It can also beused to lure babies.]]function BAIT:MoveHook(hook_phys, ply)	self:AddHookVelocity(Vector(0,0,hook_phys:GetMass() * 5))endfishing.RegisterBait(BAIT)