function fishing.GetPlayerInventorySorted(ply)	local temp = {}	ply = ply or LocalPlayer()		local inventory = fishing.GetStats(ply).inventory		if inventory then		for name, count in pairs(inventory) do			table.insert(temp, {name = name, count = count})		end				table.sort(temp, function(a,b) return a.name < b.name end)	end		return tempendfunction fishing.GetPlayerInventory(ply)	local temp = {}	ply = ply or LocalPlayer()		return fishing.GetStats(ply).inventory or {}end