local check = function(ply, ent) return ent:IsValid() and ent.IsFishingEntity and ent.player_owner ~= ply endhook.Add("CanTool", "fishing", function(ply, trace)	if check(ply, trace.Entity) then		return false	endend)hook.Add("AllowPlayerPickup", "fishing", function(ply, ent)	if check(ply, ent) then		return false	endend)hook.Add("CanPlayerUnfreeze", "fishing", function(ply, ent)	if check(ply, ent) then		return false	endend)hook.Add("PhysgunPickup", "fishing", function(ply, ent)	if check(ply, ent) then		return false	endend)hook.Add("GravGunPickupAllowed", "fishing", function(ply, ent)	if check(ply, ent) then		return false	endend)hook.Add("GravGunPunt", "fishing", function(ply, ent)	if check(ply, ent) then		return false	endend)